import React, { Component, useState } from 'react';
import FlightsTableRow from './FlightsTableRow';
import Button from './Button';
// import { routes, airlines, airports } from '../data.js'


/*
- table needs to show:
  - airline,
  src
  destination in a table
*/

const FlightsTable = ({ columns, rows, format, perPage }) => {
  const [pageNumber, updatePageNumber] = useState(1)
  const [rowsToShow, updateRows] = useState([...rows])
  const showPreviousPage = (event) => {
    updatePageNumber(pageNumber - 1)

  }

  return (
    <>
      <table>
        <thead>
          <tr>
            {columns.map((column, index) => {
              return (
                <th key={index}>{column.name}</th>
              )
            })
            }
          </tr>
        </thead>

        <tbody>
          {rows.slice(0, 24).map((row, index) => {
            return (
              <tr key={index.toString()}>
                <FlightsTableRow row={row} format={format} />
              </tr>
            )
          })}
        </tbody>
      </table>
      <p>Showing {pageNumber} - {perPage} of {rows.length} total routes.</p>
      <Button title="Previous Page" onClick={showPreviousPage} pageNumber={pageNumber} rows={rows} />
      <Button title="Next Page" onClick={showNextPage} pageNumber={pageNumber} rows={rows} />

    </>
  )
}

export default FlightsTable
