import React, { Component } from 'react';
import FlightsTableRow from './FlightsTableRow';
// import { routes, airlines, airports } from '../data.js'


/*
- table needs to show:
  - airline,
  src
  destination in a table
*/
const FlightsTable = ({ columns, rows, format }) => {

  return (
    <>
      <table>
        <thead>
          <tr>
            {columns.map((column, index) => {
              return (
                <th key={index}>{format(column.property, column.name)}</th>
              )
            })

            }
          </tr>
        </thead>
        <tbody>
          {rows.map((row, index) => {
            return (
              <tr key={index.toString()}>
                <td> {format('airline', row.airline)}</td>
                <td> {format('src', row.src)}</td>
              </tr>
            )
          })}
        </tbody>
      </table>
    </>
  )
}

export default FlightsTable
